mapper.update:
UPDATE      board_db
SET         board_count = board_count + 1
WHERE       board_id    = #{boardId}

mapper.insert:
INSERT INTO board_db
(
    user_id
    ,   title
    ,   image
    ,   content
    ,   gen_address
    ,   det_address
    ,   regdate
    ,   update_date
    ,   week
    ,   tag_big_name
    ,   tag_small_name
    ,   lat
    ,   lon
)
VALUES
(
    #{userId}
    ,   #{title}
    ,   #{image}
    ,   #{content}
    ,   #{genAddress}
    ,   #{detAddress}
    ,   NOW()
    ,   NOW()
    ,   #{week}
    ,   #{tagBigName}
    ,   #{tagSmallName}
    ,   #{lat}
    ,   #{lon}

)

mapper.update:
UPDATE board_db
SET    title            =#{title}
    ,   content         =#{content}
    ,   gen_address     =#{genAddress}
    ,   det_address     =#{detAddress}
    ,   update_date     = NOW()
    ,   week            =#{week}
    ,   tag_big_name    =#{tagBigName}
    ,   tag_small_name  =#{tagSmallName}
    ,   lat             =#{lat}
    ,   lon             =#{lon}
WHERE board_id = #{boardId}
AND   user_id = #{userId}

mapper.delete:
DELETE FROM board_db
WHERE board_id = #{boardId}
AND   user_id = #{userId}

mapper.select:
SELECT board_id as boardId , user_id as userId , title , content , gen_address as genAddress , update_date as updateDate , week , tag_big_name as tagBigName , board_count as boardCount
FROM board_db WHERE user_id = #{userId}
ORDER BY updateDate desc LIMIT #{skip}, #{amount}
