mapper.select:
SELECT board_id as boardId , index_id as indexId , title , content , gen_address as genAddress , update_date as updateDate , week , tag_big_name as tagBigName , board_count as boardCount , lat , lon FROM board_db
WHERE title LIKE concat('%', #{keyword}, '%') or content LIKE concat('%', #{keyword}, '%')
ORDER BY board_id asc LIMIT #{skip}, #{amount}

SELECT board_id as boardId , index_id as indexId , title , content , gen_address as genAddress , update_date as updateDate , week , tag_big_name as tagBigName , board_count as boardCount , lat , lon
FROM ( SELECT * FROM board_db
WHERE gen_address LIKE concat('%', #{keyword}, '%') or content LIKE concat('%', #{keyword}, '%')
)
ORDER BY board_id asc LIMIT #{skip}, #{amount}

mapper.insert:
INSERT INTO board_db
(
    index_id
    ,   title
    ,   content
    ,   gen_address
    ,   det_address
    ,   regdate
    ,   update_date
    ,   week
    ,   tag_big_name
    ,   tag_small_name
    ,   lat
    ,   lon
)
VALUES
(
    "1000"
    ,   #{title}
    ,   #{content}
    ,   #{genAddress}
    ,   #{detAddress}
    ,   sysdate()
    ,   sysdate()
    ,   #{week}
    ,   #{tagBigName}
    ,   #{tagSmallName}
    ,   #{lat}
    ,   #{lon}

)

mapper.update:
UPDATE board_db
SET    title            =#{title}
    ,   content         =#{content}
    ,   gen_address     =#{genAddress}
    ,   det_address     =#{detAddress}
    ,   update_date     = sysdate()
    ,   week            =#{week}
    ,   tag_big_name    =#{tagBigName}
    ,   tag_small_name  =#{tagSmallName}
    ,   lat             =#{lat}
    ,   lon             =#{lon}
WHERE board_id = #{boardId}
AND   index_id = #{indexId}

mapper.delete:
DELETE FROM board_db
WHERE board_id = #{boardId}
AND   index_id = #{indexId}
